import classes from "./Cart.module.css";


import Modal from "../UI/Modals";

const Cart = (props) => {
  const cartItems = (
    <ul className={classes["cart-items"]}>
      {[{ id: "c1", name: "Kitfo", amount: 2, price: 49.99 }].map((item) => (
        <li> {item.name}</li>
      ))}
    </ul>
  );
  return (
    <Modal>
      {cartItems}
      <div className={classes.total}>
        <span> Total Amount</span>
        <span> 49.99</span>
      </div>

      <div className={classes.actions}>
        <button className={classes['button--a']} onClick={props.om}> Close</button>
        <button className={classes.button}>Order</button>

      </div>
    </Modal>
  );
};

export default Cart;
